$(function() {
  'use strict';

  $('.preloader').fadeOut();
  // ==============================================================
  // Theme options
  // ==============================================================
  // ==============================================================
  // sidebar-hover
  // ==============================================================

  $('.left-sidebar').hover(
    function() {
      $('.navbar-header').addClass('expand-logo');
    },
    function() {
      $('.navbar-header').removeClass('expand-logo');
    }
  );
  // this is for close icon when navigation open in mobile view
  $('.nav-toggler').on('click', function() {
    $('#main-wrapper').toggleClass('show-sidebar');
    $('.nav-toggler i').toggleClass('ti-menu');
  });
  $('.nav-lock').on('click', function() {
    $('body').toggleClass('lock-nav');
    $('.nav-lock i').toggleClass('mdi-toggle-switch-off');
    $('body, .page-wrapper').trigger('resize');
  });
  $('.search-box a, .search-box .app-search .srh-btn').on('click', function() {
    $('.app-search').toggle(200);
    $('.app-search input').focus();
  });

  // ==============================================================
  // Clock Display
  // ==============================================================
  $(document).ready(function() {
    clockUpdate();
    setInterval(clockUpdate, 1000);
  });
  
  function clockUpdate() {
    var date = new Date();
    $('#clockDisplay').css({ color: '#fff', 'text-shadow': '0 0 6px #ff0' });
   
    function addZero(x) {
      if (x < 10) {
        return (x = '0' + x);
      } else {
        return x;
      }
    }

    function twelveHour(x) {
      if (x > 12) {
        return (x = x - 12);
      } else if (x == 0) {
        return (x = 12);
      } else {
        return x;
      }
    }

    function getAmPm(x) {
        return x < 12 ? 'AM' : 'PM';
    }

    function getYear(x){
        if (x < 1000) {
            x += 1900;
        }

        return x;
    }

    function getMonth(x) {
        var monthArray = new Array("Jan", "Feb", "Mar", "Apr", "May", "Jun", "Jul", "Aug", "Sep", "Oct", "Nov", "Dec");
        return monthArray[x];
    }

    function getDay(x) {
        
    }

    function getDayOfWeek(x) {
        var dayArray = new Array("Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday");
        return dayArray[x];
    }

    var year = getYear(date.getFullYear());
    var month = getMonth(date.getMonth());
    var day = date.getDate();
    var dayOfWeek = getDayOfWeek(date.getDay())
    var h = addZero(twelveHour(date.getHours()));
    var m = addZero(date.getMinutes());
    var s = addZero(date.getSeconds());
    var t = getAmPm(date.getHours());

    $('#clockDisplay').text(`${dayOfWeek}, ${day} ${month} ${year} | ${h}:${m}:${s} ${t}`);
  }

  // ==============================================================
  // Right sidebar options
  // ==============================================================
  $(function() {
    $('.service-panel-toggle').on('click', function() {
      $('.customizer').toggleClass('show-service-panel');
    });
    $('.page-wrapper').on('click', function() {
      $('.customizer').removeClass('show-service-panel');
    });
  });
  // ==============================================================
  // This is for the floating labels
  // ==============================================================
  $('.floating-labels .form-control')
    .on('focus blur', function(e) {
      $(this)
        .parents('.form-group')
        .toggleClass('focused', e.type === 'focus' || this.value.length > 0);
    })
    .trigger('blur');

  // ==============================================================
  //tooltip
  // ==============================================================
  $(function() {
    $('[data-toggle="tooltip"]').tooltip();
  });
  // ==============================================================
  //Popover
  // ==============================================================
  $(function() {
    $('[data-toggle="popover"]').popover();
  });

  // ==============================================================
  // Perfact scrollbar
  // ==============================================================
  $('.message-center, .customizer-body, .scrollable').perfectScrollbar({
    wheelPropagation: !0
  });

  /*var ps = new PerfectScrollbar('.message-body');
    var ps = new PerfectScrollbar('.notifications');
    var ps = new PerfectScrollbar('.scroll-sidebar');
    var ps = new PerfectScrollbar('.customizer-body');*/

  // ==============================================================
  // Resize all elements
  // ==============================================================
  $('body, .page-wrapper').trigger('resize');
  $('.page-wrapper').show();
  // ==============================================================
  // To do list
  // ==============================================================
  $('.list-task li label').click(function() {
    $(this).toggleClass('task-done');
  });

  //****************************
  /* This is for the mini-sidebar if width is less then 1170*/
  //****************************
  var setsidebartype = function() {
    var width = window.innerWidth > 0 ? window.innerWidth : this.screen.width;
    if (width < 1170) {
      $('#main-wrapper').attr('data-sidebartype', 'mini-sidebar');
    } else {
      $('#main-wrapper').attr('data-sidebartype', 'full');
    }
  };
  $(window).ready(setsidebartype);
  $(window).on('resize', setsidebartype);
  //****************************
  /* This is for sidebartoggler*/
  //****************************
  $('.sidebartoggler').on('click', function() {
    $('#main-wrapper').toggleClass('mini-sidebar');
    if ($('#main-wrapper').hasClass('mini-sidebar')) {
      $('.sidebartoggler').prop('checked', !0);
      $('#main-wrapper').attr('data-sidebartype', 'mini-sidebar');
    } else {
      $('.sidebartoggler').prop('checked', !1);
      $('#main-wrapper').attr('data-sidebartype', 'full');
    }
  });
});
